import React from 'react'
import { useState } from "react";
import axios from "axios";

const SignupPage = () => {
    const[username,setUsername]=useState(null)
    const[firstname,setFname]=useState(null)
    const[lastname,setLname]=useState(null)
    const[Email,setEmail]=useState(null)
    const[Password,setPassword]=useState(null)
    const[Phone,setPhone]=useState(null)
    const[address,setAddress]=useState(null)

    function CreateAccount(){
        console.log("Firstname"+firstname)
        console.log("Lastname"+lastname)
        console.log("phone_number"+Phone)
        console.log("password"+Password)
        console.log("address"+address)
    }CreateAccount()

    const CreateAccounts=async()=>{
      const data=new FormData()
      data.append("first_name",firstname)
      data.append("last_name",lastname)
      data.append("phone_number",Phone)
      data.append("password",Password)
      data.append("address",address)
      const response=await axios.post("https://gist.manojkoravangi.com/insertuserdata.php",data,{header:{"content-type":"multipart/form-data"}})
      if(response){
        if(response.data.status==="success"){
          alert("Account Created")
        }
        else{
          alert("Account creation failed")
        }
      }
    }

  return (<div className="col-4 mx-auto p-3 m-5 rounded border">
        <h4 className='text-primary'>Signin</h4>
        <input onChange={(event)=>{setFname(event.target.value)}} placeholder="First name" className="form-cotrol mt-3 w-100"></input>
        <input onChange={(event)=>{setLname(event.target.value)}} placeholder="Last name" className="form-cotrol mt-3 w-100 d-flex"></input>
        <input onChange={(event)=>{setPhone(event.target.value)}} placeholder="Phone" className="form-cotrol mt-3 w-100 d-flex"></input>
        <input onChange={(event)=>{setPassword(event.target.value)}} placeholder="password" className="form-cotrol mt-3 w-100"></input>
        <button class="btn btn-warning w-100 mt-3" onClick={()=>CreateAccounts()}>Signin</button>
    </div>)
}

export default SignupPage
